var algemeen = function () {

	this.SpecReporter = function(){
		let SpecReporter = require('jasmine-spec-reporter').SpecReporter;
    	jasmine.getEnv().addReporter(new SpecReporter({displayStacktrace: 'all'}));
	};

	this.PrettyReporter = function(){
	    var PrettyReporter = require('protractor-pretty-html-reporter').Reporter;
		    var prettyReporter = new PrettyReporter({
		        path: __dirname+'\\..\\results',
		        screenshotOnPassed: true
		    });
	    jasmine.getEnv().addReporter(prettyReporter);
    };



};
module.exports = new algemeen();